import {
  Bridge,
  SDK,
  Client,
  DefaultFlagExport,
  Global,
} from './lib';

declare global {
  /**
   * 业务侧自行通过声明合并，对 jsapi 进行拓展。
   */
  export interface Extra4JSAPI {
  }

  export const dd: Bridge<{
    flag: DefaultFlagExport;
    sdk: SDK.v0_0_0;
    client: Client.v0_0_0;
    namespace: void;
    extra: Extra4JSAPI;
  }>;

  /**
   * 使用插件提供的 JS 接口，函数返回值为 `插件` 通过 `main` 字段暴露的 JS 接口。
   */
  export const requirePlugin: Global.requirePlugin;
}
